import React from 'react';
import './styles/App.css'
import Header from './components/header/Header';
import Input from './components/input/Input';
import SubmitButton from './components/button/SubmitButton';
import Item from './components/item/Item';

function App() {

  const [items, setItems] = React.useState([]);
  const [inputValue, setInputValue] = React.useState('')
  const [enterPressed, setEnterPressed] = React.useState(false);

  const handleEnterPressed = () => {
    setEnterPressed(true);
  }

  const handleChangeInputValue = (value) => {
    setInputValue(value);
  }

  const handleAddItem = () => {
    if(inputValue && inputValue.trim()){
      const newItem = {
        id : items.length,
        value : inputValue
      }
      setItems(prevState => {
        return [...prevState, newItem]}
      );
      setInputValue('')
    }
  }

  const handleRemoveItem = (id) => {
    const arr = items.filter((i) => i.id !== id);
    setItems(arr);
  }

  return (
    <div className="App">
      <div className="container">

        <Header title={'ToDo List'}/>

        <div className='content_parent'>

          <div className='input_container'>
            <Input inputValue={inputValue} handleChangeInputValue={handleChangeInputValue} handleEnterPressed={handleEnterPressed}/>
            <SubmitButton handleAddItem={handleAddItem} enterPressed={enterPressed}/>
          </div>

          <ol className='list'>
              {items.map((item) => <Item id={item?.id} item={item} removeItem={handleRemoveItem}/>)}
          </ol>

        </div>
      </div>
    </div>
  );
}

export default App;
